    //fails on 30 / 42
    static List<String> findAllConcatenatedWordsInADict(String[] words) {
        TrieNode[] alph = new TrieNode[26];
        //build tries
        for (String w : words) {
            if (w.length() == 0) continue;
            char first = w.charAt(0);
            if (alph[first - 'a'] == null) alph[first - 'a'] = new TrieNode(first, false);
            if (w.length() == 1) alph[first - 'a'].isWord = true;
            TrieNode node = alph[first - 'a'];
            for (int i = 1; i < w.length(); i++) {
                char c = w.charAt(i);
                if (node.children == null) node.children = new ArrayList<>();
                Optional<TrieNode> nodeOptional = node.children.stream().filter(n -> n.letter == c).findFirst();
                if (!nodeOptional.isPresent()) {
                    TrieNode nodeNew = new TrieNode(c, i == w.length() - 1);
                    node.children.add(nodeNew);
                    node = nodeNew;
                } else {
                    node = nodeOptional.get();
                }
            }
        }

        Set<String> set = new HashSet<>();
        for (int i = 0; i < words.length; i++) {
            String w = words[i];
            goRecursive(w, w, words, alph, set, 0);
        }
        return new ArrayList<>(set);
    }

    private static void goRecursive(String currWord,String word, String[] words, TrieNode[] alph, Set<String> set, int count) {
        if (currWord.length() == 0) {
            if (count > 1) set.add(word);
            return;
        }
        if (currWord.length() == 1) {
            TrieNode node = alph[currWord.charAt(0) - 'a'];
            if (node != null && node.isWord) {
                goRecursive("", word, words, alph, set, count + 1);
            } else return;
        }
        int first = currWord.charAt(0);
        TrieNode node = alph[first - 'a'];
        if (node == null) return;
        if (node.isWord) goRecursive(currWord.substring(1), word, words, alph, set, count + 1);
        for (int i = 1; i < currWord.length(); i++) {
            char c = currWord.charAt(i);
            if (node.children == null) return;
            Optional<TrieNode> curNodeOptional = node.children.stream().filter(n -> n.letter == c).findFirst();
            if (curNodeOptional.isPresent()) {
                TrieNode curNode = curNodeOptional.get();
                node = curNode;
                if (curNode.isWord) {
                    goRecursive(currWord.substring(i + 1), word, words, alph, set, count + 1);
                } else break;
            }
        }
    }


    static class TrieNode {
        char letter;
        boolean isWord;
        List<TrieNode> children;

        public TrieNode(char letter, boolean isWord) {
            this.letter = letter;
            this.isWord = isWord;
        }
    }

    static List<String> findAllConcatenatedWordsInADict1(String[] words) {
        Set<String> set = new HashSet<>();
        for (int i = 0; i < words.length; i++) {
            String a = words[i];
            for (int j = 0; j < words.length; j++) {
                //case when one string occurs 2 times
                if (i == j) continue;

                String b = words[j];
                if (a.length() * 2 <= b.length()) {
                    int idx = b.indexOf(a);
                    if (idx > -1 && b.indexOf(a, idx + a.length()) > -1) {
                        set.add(b);
                        continue;
                    }
                }
                for (int k = 0; k < words.length; k++) {
                    if (i == k || j == k) continue;
                    String c = words[k];
                    if (a.length() + b.length() > c.length()) break;
                    if (c.contains(a) && c.contains(b)) set.add(c);
                }
            }
        }
        return new ArrayList<>(set);
    }
