    //https://leetcode.com/problems/3sum/solutions/2710157/java-o-n-log-n-n-n-log-n-solution/
    //Runtime
    //1618 ms
    //Beats
    //5%
    //Memory
    //148.8 MB
    //Beats
    //7.5%
    static List<List<Integer>> threeSum(int[] nums) {
        Arrays.sort(nums); // n ^ 2
        Set<List<Integer>> result = new HashSet<>();
        for (int i = 0; i < nums.length - 1; i++) {
            int one = nums[i];
            for (int j = i; j < nums.length; j++) {
                if (i == j) continue;
                int two = nums[j];
                int three = 0 - one - two;
                int idxThree = Arrays.binarySearch(nums, three);
                if (idxThree >= 0 && idxThree != i && idxThree != j) {
                    List<Integer> list = new ArrayList<>();
                    if (three <= one) {
                        list.add(three);
                        list.add(one);
                        list.add(two);
                    } else if (three <= two) {
                        list.add(one);
                        list.add(three);
                        list.add(two);
                    } else {
                        list.add(one);
                        list.add(two);
                        list.add(three);
                    }
                    //Collections.sort(list);
                    result.add(list);
                }
            }
        }
        return new ArrayList<>(result);
    }

    static List<List<Integer>> threeSum5(int[] nums) {
        //num -> frequency
        Arrays.sort(nums);
        Map<Integer, Integer> map = new HashMap<>();
        for (int i = 0; i < nums.length; i++) {
            if (map.containsKey(nums[i])) {
                map.put(nums[i], map.get(nums[i]) + 1);
            } else {
                map.put(nums[i], 1);
            }
        }
        Set<Integer> set = map.keySet();
        Set<List<Integer>> result = new HashSet<>();
        for (int three : set) {
            for (int i = 0; i < nums.length; i++) {
                int one = nums[i];
                int two = three + one;
                if (set.contains(-two)) {

                    List<Integer> list = new ArrayList<>();
                    list.add(one);
                    list.add(-two);
                    list.add(three);
                    Collections.sort(list);
                    result.add(list);
                }
            }
        }
        return new ArrayList<>(result);
    }

