    static List<List<Integer>> threeSum2(int[] nums) {
        Arrays.sort(nums);
        //sum -> set of indices
        Map<Integer, Set<List<Integer>>> map = new HashMap<>();
        for (int i = 0; i < nums.length - 1; i++) {
            for (int j = i + 1, k = 0; j < nums.length; j++) {
                int sum = nums[i] + nums[j];
                List<Integer> pair = new ArrayList<>();
                pair.add(i);
                pair.add(j);
                pair.add(k);
                if (!map.containsKey(sum)) {
                    Set<List<Integer>> set = new HashSet<>();
                    set.add(pair);
                    map.put(sum, set);
                } else {
                    map.get(sum).add(pair);
                }
            }
        }
        Set<List<Integer>> res = new HashSet<>();
        for (int i = 0; i < nums.length; i++) {
            Set<List<Integer>> set = map.get(-nums[i]);
            if (set != null) {
                for (List<Integer> pair : set){
                    int one = pair.get(0), two = pair.get(1);
                    if (i != one && i != two && one != two) {
                        List<Integer> list = new ArrayList<>();
                        list.add(nums[one]);
                        list.add(nums[two]);
                        list.add(nums[i]);
                        Collections.sort(list);
                        res.add(list);
                    }
                }
            }
        }
        return new ArrayList<>(res);
    }

